import { Link, NavLink } from "react-router-dom";
import { useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { toast, ToastContainer } from "react-toastify";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { addUser } from "../Redux/Features/User/userSlice";
export default function Header() {
  const dispatch = useDispatch();
  const location = useLocation();
  axios.defaults.withCredentials = true;
  const [isloggedin, setIsloggedin] = useState();
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State to toggle the mobile menu
  const navigate = useNavigate();
  const user = useSelector((state) => state.user);
  useEffect(() => {
    axios.get(`${import.meta.env.VITE_BACKEND_URL}/api/test`).then((res) => {
      console.log(res.data);
      setIsloggedin(res.data.success);
    });
  }, [location]);

  const HandleLogout = () => {
    axios.get(`${import.meta.env.VITE_BACKEND_URL}/api/logout`).then((res) => {
      const data = {
        status: "error",
        message: "You are not logged in",
      };
      dispatch(addUser(data));
      toast.success(res.data.message, { theme: "dark" });
      setTimeout(() => {
        navigate("/");
      }, 3000);
    });
  };

  return (
    <>
      <ToastContainer />
      <header className="shadow sticky z-50 top-0">
        <nav className="bg-gray-900 border-gray-700 px-4 lg:px-6 py-2.5">
          <div className="flex flex-wrap justify-between items-center mx-auto max-w-screen-xl">
          <Link to="/" className="flex items-center">
              <img
                src="data:image/png;base64,"
                className="mr-1 h-20"
                alt="Logo"
              />
            </Link>

            {/* Hamburger menu button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="lg:hidden text-gray-400 hover:text-white focus:outline-none"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M4 6h16M4 12h16m-7 6h7"
                ></path>
              </svg>
            </button>

            <div
              className={`justify-between items-center w-full lg:flex lg:w-auto lg:order-1 ${
                isMenuOpen ? "block" : "hidden"
              }`}
              id="mobile-menu-2"
            >
              <ul className="flex flex-col mt-4 font-medium lg:flex-row lg:space-x-8 lg:mt-0">
                <li>
                  <NavLink
                    to="/"
                    className={({ isActive }) =>
                      `block py-2 pr-4 pl-3 duration-200 ${
                        isActive ? "text-orange-500" : "text-gray-300"
                      } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                    }
                  >
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/About"
                    className={({ isActive }) =>
                      `block py-2 pr-4 pl-3 duration-200 ${
                        isActive ? "text-orange-500" : "text-gray-300"
                      } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                    }
                  >
                    About
                  </NavLink>
                </li>
                <li>
                  <NavLink
                    to="/Contact"
                    className={({ isActive }) =>
                      `block py-2 pr-4 pl-3 duration-200 ${
                        isActive ? "text-orange-500" : "text-gray-300"
                      } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                    }
                  >
                    Contact
                  </NavLink>
                </li>
                {user.status === "success" && (
                  <li>
                    <NavLink
                      to={`/SubmitHistoryPage/${user.id}`}
                      className={({ isActive }) =>
                        `block py-2 pr-4 pl-3 duration-200 ${
                          isActive ? "text-orange-500" : "text-gray-300"
                        } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                      }
                    >
                      View Submit History
                    </NavLink>
                  </li>
                )}
                {user.role === "admin" && (
                  <li>
                    <NavLink
                      to="/Adminpage"
                      className={({ isActive }) =>
                        `block py-2 pr-4 pl-3 duration-200 ${
                          isActive ? "text-orange-500" : "text-gray-300"
                        } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                      }
                    >
                      Admin Dashboard
                    </NavLink>
                  </li>
                )}
                {isloggedin ? (
                  <li>
                    <NavLink
                      onClick={HandleLogout}
                      className={({ isActive }) =>
                        `block py-2 pr-4 pl-3 duration-200 ${
                          isActive ? "text-red-500" : "text-gray-300"
                        } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                      }
                    >
                      Logout
                    </NavLink>
                  </li>
                ) : (
                  <>
                    <li>
                      <NavLink
                        to="/signin"
                        className={({ isActive }) =>
                          `block py-2 pr-4 pl-3 duration-200 ${
                            isActive ? "text-orange-500" : "text-gray-300"
                          } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                        }
                      >
                        Login
                      </NavLink>
                    </li>
                    <li>
                      <NavLink
                        to="/signup"
                        className={({ isActive }) =>
                          `block py-2 pr-4 pl-3 duration-200 ${
                            isActive ? "text-orange-500" : "text-gray-300"
                          } border-b border-gray-800 hover:bg-gray-800 lg:hover:bg-transparent lg:border-0 hover:text-orange-500 lg:p-0`
                        }
                      >
                        Register
                      </NavLink>
                    </li>
                  </>
                )}
              </ul>
            </div>
          </div>
        </nav>
      </header>
    </>
  );
}
